import React, { useState } from 'react';
import './styles.css';

const EventHandlingExample = () => {
  // State to hold the count
  const [count, setCount] = useState(0);
  // State to hold the input name
  const [name, setName] = useState('');
  // State to track the current theme
  const [theme, setTheme] = useState('light');

  // Event handler function to handle button click
  const handleClick = () => {
    // Increment the count when button is clicked
    setCount(count + 1);
    // Display an alert when count is incremented
    alert('Count incremented!');
  };

  // Event handler function to reset the count
  const handleReset = () => {
    // Set the count back to zero
    setCount(0);
    // Display an alert when count is reset
    alert('Count reset!');
  };

  // Event handler function to handle form submission
  const handleSubmit = (event) => {
    // Prevent default form submission behavior
    event.preventDefault();
    // Display an alert with the entered name
    alert(`Hello ${name}!`);
  };

  // Event handler function to handle input change
  const handleInputChange = (event) => {
    // Update the name state with the input value
    setName(event.target.value);
  };

  // Event handler function to toggle theme
  const toggleTheme = () => {
    // Toggle between light and dark theme
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  return (
    <div className={`container ${theme === 'dark' ? 'dark-theme' : ''}`}>
      {/* Header */}
      <div className="header">
        <h1>Header</h1>
      </div>

      {/* Content */}
      <div className="content">
        {/* Display the count */}
        <p className="count">Count: {count}</p>
        {/* Button to trigger the event */}
        <button className="button" onClick={handleClick}>Increment Count</button>
        {/* Button to reset the count */}
        <button className="button" onClick={handleReset}>Reset Count</button>

        {/* Form to input name */}
        <form onSubmit={handleSubmit}>
          <label>
            Enter Your Name:
            {/* Input field for name */}
            <input type="text" value={name} onChange={handleInputChange} className="input" />
          </label>
          {/* Button to submit the form */}
          <button type="submit" className="submit-button">Submit</button>
        </form>
      </div>

      {/* Footer */}
      <div className="footer">
        <h3>Footer</h3>
      </div>

      {/* Button to toggle theme */}
      <button className="theme-button" onClick={toggleTheme}>Toggle Theme</button>
    </div>
  );
};

export default EventHandlingExample;
