import React, { useState } from 'react';
import './styles.css';

const EventHandlingExample = () => {
  const [count, setCount] = useState(0);
  const [name, setName] = useState('');
  const [data, setData] = useState([]);

  const handleClick = () => {
    setCount(count + 1);
    alert('Count incremented!');
  };

  const handleReset = () => {
    setCount(0);
    alert('Count reset!');
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(`Hello ${name}!`);

    // Add count and name to data array
    setData([...data, { count, name }]);
    // Clear the name input field after submission
    setName('');
  };

  const handleInputChange = (event) => {
    setName(event.target.value);
  };

  return (
    <div className="container">
      <div className="header">
        <h1>Header</h1>
      </div>

      <div className="content">
        <p className="count">Count: {count}</p>
        <button className="button" onClick={handleClick}>Increment Count</button>
        <button className="button" onClick={handleReset}>Reset Count</button>

        <form onSubmit={handleSubmit}>
          <label>
            Enter Your Name:
            <input type="text" value={name} onChange={handleInputChange} className="input" />
          </label>
          <button type="submit" className="submit-button">Submit</button>
        </form>

        <table className="data-table">
          <thead>
            <tr>
              <th>Count</th>
              <th>Name</th>
            </tr>
          </thead>
          <tbody>
            {data.map((item, index) => (
              <tr key={index}>
                <td>{item.count}</td>
                <td>{item.name}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="footer">
        <h3>Footer</h3>
      </div>
    </div>
  );
};

export default EventHandlingExample;
