import React, { useState } from 'react';
import './styles.css';
import axios from 'axios';

const EventHandlingExample = () => {
  const [count, setCount] = useState(0);
  const [name, setName] = useState('');

  const handleClick = () => {
    setCount(count + 1);
    alert('Count incremented!');
  };

  const handleReset = () => {
    setCount(0);
    alert('Count reset!');
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(`Hello ${name}!`);

    // Send count and name data to Google Sheets
    const formData = new FormData();
    formData.append('count', count);
    formData.append('name', name);

    axios.post('YOUR_GOOGLE_SHEETS_ENDPOINT', formData)
      .then(response => {
        console.log('Data sent successfully:', response.data);
      })
      .catch(error => {
        console.error('Error sending data:', error);
      });
  };

  const handleInputChange = (event) => {
    setName(event.target.value);
  };

  return (
    <div className="container">
      <div className="header">
        <h1>Header</h1>
      </div>

      <div className="content">
        <p className="count">Count: {count}</p>
        <button className="button" onClick={handleClick}>Increment Count</button>
        <button className="button" onClick={handleReset}>Reset Count</button>

        <form onSubmit={handleSubmit}>
          <label>
            Enter Your Name:
            <input type="text" value={name} onChange={handleInputChange} className="input" />
          </label>
          <button type="submit" className="submit-button">Submit</button>
        </form>
      </div>

      <div className="footer">
        <h3>Footer</h3>
      </div>
    </div>
  );
};

export default EventHandlingExample;
